    DECLARE
        TYPE ptype IS RECORD(nam VARCHAR2(512), val VARCHAR2(512));
        TYPE paramstype IS TABLE OF ptype;
        commonparams       paramstype;
        banner             VARCHAR2(80);
        db_name            VARCHAR2(9);
        cpus               NUMBER;
        cpusers            NUMBER;
        def_servers_max    NUMBER;
        def_servers_target NUMBER;
        granule_size       NUMBER;
