i.block_gets, i.consistent_gets , i.physical_reads, i.block_changes , i.consistent_changes
FROM v$session s, v$sess_io i, v$sql sql, v$process p
WHERE 
s.sid = i.sid 
and s.paddr      = p.addr 
and s.sql_address=sql.address ORDER BY BLOCK_CHANGES desc 
