function kill_hanging_ports {
 for port in $1; do
  misbehaved=$(lsof -i TCP:$port |grep "^java" |grep $apuser |wc -l)
  if [ "$misbehaved" -gt 0 ]; then
    log_message "Port $port: killing hanging processes: $misbehaved"
