function rotate_pools {
  for p in $1; do
    date >> $APPLTMP/lsof_pre_$p.log
    lsof -i TCP:$p >> $APPLTMP/lsof_pre_$p.log
    lsof -i TCP:$p | grep -v MWADIS | awk '{print $2}'| xargs kill -9 >/dev/null 2>&1
