miko CRM (conflict resolution manager) concurren request list

set linesize 400 pagesize 600
column start_time format a17
column spid format a7
column audsid format a7
column pid format a7
column sid format a7
column os_process format a7
column phase format a10
column status format a10
column user_concurrent_program_name format a15
column CONCURRENT_PROGRAM format a50
column "CRM wait time" format a30

SELECT ( SELECT SUBSTR ( fu.user_name, 1, 10 )
           FROM fnd_user fu
          WHERE fu.user_id = cr.requested_by
        ) user_name,
       TO_CHAR ( cr.requested_start_date , 'DD-MON-YYYY HH24:MI' ) requested_time,
       TO_CHAR ( cr.CRM_RELEASE_DATE , 'DD-MON-YYYY HH24:MI' ) CRM_RELEASE_DATE,
       TO_CHAR ( NVL ( cr.actual_start_date, cr.requested_start_date ), 'DD-MON-YYYY HH24:MI' ) start_time,
	TO_CHAR ( ACTUAL_COMPLETION_DATE, 'DD-MON-YYYY HH24:MI' ) end_time,
       cr.request_id request_id,
        trunc(ROUND((NVL(cr.CRM_RELEASE_DATE, sysdate) - cr.requested_start_date)*1440, 2)/60,0) ||':hrs ' || 
	trunc((round((NVL(cr.CRM_RELEASE_DATE, sysdate) - cr.requested_start_date)*1440, 2))-(trunc(ROUND((NVL(cr.CRM_RELEASE_DATE, sysdate) - cr.requested_start_date)*1440, 2)/60,0)*60),0)|| ':Mins ' || 
	trunc(mod((NVL(cr.CRM_RELEASE_DATE, sysdate) - cr.requested_start_date)*1440*60, 60), 0)|| ':Secs' "CRM wait time",	
       ( SELECT SUBSTR ( lu.meaning, 1, 10 )
           FROM fnd_lookups lu
          WHERE lu.lookup_type = 'CP_PHASE_CODE'
            AND lu.lookup_code = cr.phase_code ) phase,
       ( SELECT SUBSTR ( LTRIM ( lu.meaning ), 1, 11 )
           FROM fnd_lookups lu
          WHERE lu.lookup_type = 'CP_STATUS_CODE'
            AND lu.lookup_code = cr.status_code ) status,
cr.status_code,
       SUBSTR ( cr.oracle_process_id, 1, 6 ) spid,
       SUBSTR ( cr.os_process_id, 1, 10 ) os_process,
       SUBSTR ( TO_CHAR ( cr.oracle_session_id ), 1, 6 ) audsid,
        ( SELECT SUBSTR ( cp.concurrent_program_name || ' - ' ||
                 cpl.user_concurrent_program_name, 1, 40 )
            FROM fnd_concurrent_programs    cp,
                 fnd_concurrent_programs_tl cpl
           WHERE cp.application_id         = cr.program_application_id
             AND cp.concurrent_program_id  = cr.concurrent_program_id
             AND cpl.application_id        = cr.program_application_id
             AND cpl.concurrent_program_id = cr.concurrent_program_id
             AND cpl.language              = USERENV('LANG') 
         ) concurrent_program
  FROM fnd_concurrent_requests cr,
       v$process               vp,
       fnd_concurrent_programs cp1,
       gv$session               vs
 WHERE cr.oracle_process_id = vp.spid(+)
   AND cr.oracle_session_id = vs.audsid(+)
   AND cp1.concurrent_program_id  = cr.concurrent_program_id
--  and cr.oracle_process_id = &oracle_process_id
-- and cr.request_id in ( 13135362 )
--   AND cr.phase_code = 'R'
--  and cr.status_code = 'R'
--   AND cr.phase_code  NOT IN ('I','C')
--   AND cr.status_code NOT IN ('U','X','D','E','I','C')
--and cp1.concurrent_program_name like '%XX%'
-- and vs.INST_ID is not NULL  
--and TO_CHAR ( NVL ( cr.actual_start_date, cr.requested_start_date ), 'DD-MON-YYYY HH24' ) in ( '17-MAR-2008 09', '17-MAR-2008 10', '17-MAR-2008 11')
and trunc(ROUND((cr.CRM_RELEASE_DATE -  cr.requested_start_date)*1440, 2),0) > 1  -- pending in CRM queue longer then x minutes
and trunc(cr.CRM_RELEASE_DATE) >= trunc( sysdate) -0 --(0=today)
 ORDER BY 
       requested_time,
       start_time;

