  begin
     my_sqltext := 'SELECT  CCG . cost_group cg_name, 
        CCG . description cg_description, 
        MSI.SEGMENT1 cg_item, 
        ROUND ( SUM  ( CPCS . accounted_value ) * :P_EXCHANGE_RATE / :P_ROUND_UNIT ) * :P_ROUND_UNIT cg_acc_val , 
        ROUND ( SUM  ( CPCS . rollback_value )  * :P_EXCHANGE_RATE / :P_ROUND_UNIT ) * :P_ROUND_UNIT cg_onh_val , 
        ROUND ( SUM  ( CPCS . rollback_value    - CPCS . accounted_value ) * :P_EXCHANGE_RATE / :P_ROUND_UNIT ) * :P_ROUND_UNIT cg_adj_val 
FROM    cst_cost_groups CCG        , 
        mtl_system_items_b_kfv MSI , 
        cst_period_close_summary CPCS 
WHERE   CPCS.cost_group_id         = CCG.cost_group_id (+) 
        AND CPCS.inventory_item_id = MSI.inventory_item_id 
        AND MSI.organization_id    = :p_org_id 
