function move_files () {
if [ ${#} != 2 ]; then
echo "sourec and destination directory parameters are mandatory"
  exit 1
fi
SRC_PATH=${1}
DST_PATH=${2}


if [ ! -d "${SRC_PATH}" ]; then
  echo "${SRC_PATH} does not exists."
   exit 1
fi
if [ ! -d "${DST_PATH}" ]; then
  echo "${DST_PATH} does not exists."
   exit 1
fi



echo "moving files from ${SRC_PATH} to ${DST_PATH}"
for FILE in `ls ${SRC_PATH}`
do
  FULL_SF=${SRC_PATH}/${FILE}
  FULL_DF=${DST_PATH}/${FILE}
  if [[ ! -f ${FULL_DF} ]]; then
    if [[ -f "${FULL_SF}" ]]; then
       cp ${FULL_SF} ${FULL_DF}
       if [ $? = 0 ]; then
         rm ${FULL_SF}
         if [ $? = 0 ]; then
           echo "${FULL_SF} moved successfully."
         else
           echo "error rm file ${FULL_SF}"
         fi
       else
         echo "error to move file ${FULL_SF} skiping file."
       fi
    else
      echo "${FULL_SF} does not exists."
    fi
  else
    echo "${FULL_DF} exists skiping file."
  fi
done
}

move_files ${A} ${C}
move_files ${B} ${C}

exit 0
